INSERT INTO `pages` (`id`, `title`, `slug`, `description`, `template`, `is_active`, `created_at`, `updated_at`) VALUES (4, 'Gizlilik Politikası', 'gizlilik-politikasi', '<p style=\"color: #333; font-size: 16px; line-height: 1.8; margin-bottom: 20px;\">Gizliliğiniz bizim için önemlidir. Kolej İntegral olarak kişisel verilerinizi yalnızca ilgili mevzuata uygun şekilde ve hizmetlerimizi sunabilmek amacıyla işlemekteyiz. Detaylı gizlilik politikası içeriği bu alanda yayınlanacaktır.</p>', 'legal', 1, '2025-12-02 21:18:02', '2025-12-02 22:39:10');
